    public List<Integer> findSubstring(String s, String[] words) {
        HashMap<String, Integer> wordsMap = new HashMap<>();
        for (String word : words) {
            wordsMap.put(word, wordsMap.getOrDefault(word, 0) + 1);
        }
        List<Integer> res = new ArrayList<>();
        int width = words[0].length();
        Set<String> uniqueWords = wordsMap.keySet();
        for (String word : uniqueWords) {
            int fromIndex = 0;
            int start = 0;
            while ((start = s.indexOf(word, fromIndex)) != -1) {
                if (start + 1 + width * (words.length - 1) > s.length()) {
                    break;
                }
                int c = wordsMap.get(word);
                wordsMap.put(word, c - 1);
                f(start, start + width, width, s, wordsMap, uniqueWords, res);
                wordsMap.put(word, c);
                fromIndex = start + 1;
            }
        }
        return res;
    }

    public void f(int start, int left, int width, String s, Map<String, Integer> wordsMap, Set<String> words, List<Integer> res) {
        boolean fg = false;
        for (String word : words) {
            if (wordsMap.get(word) != 0) {
                fg = true;
                break;
            }
        }
        if (!fg) {
            res.add(start);
            return;
        }
        for (String word : words) {
            int c = wordsMap.get(word);
            if (wordsMap.size() == 1) {
                StringBuilder sb = new StringBuilder();
            }
            if (c == 0) {
                continue;
            }
            String sub = s.substring(left, left + width);
            if (sub.equals(word)) {
                wordsMap.put(word, c - 1);
                f(start, left + width, width, s, wordsMap, words, res);
                wordsMap.put(word, c);
            }
        }
    }
